import { View } from 'react-native';
import { withTheme, Appbar, Card, Text, ProgressBar, Button, FAB } from 'react-native-paper';

function HomeScreen({ navigation }) {
	return (
		<>
			<Appbar.Header>
				<Appbar.Content title="LMS" />
			</Appbar.Header>

			<Text style={{ marginHorizontal: 10, marginVertical: 20 }} variant="titleLarge">Headline Large</Text>

			<Card style={{ margin: 10 }} onPress={() => navigation.navigate('KelasScreen')}>
				<Card.Content>
					<Text variant="titleMedium">Akademi Bank Sampah</Text>

					<View style={{ marginVertical: 15 }}>
						<Text variant="labelSmall">Perkembangan: 70%</Text>
						<ProgressBar progress={0.7} />
					</View>

					<Text>Kelas ini mengajarkan peserta untuk memiliki pengetahuan & keterampilan dalam pengolahan sampah</Text>
				</Card.Content>
				<Card.Actions>
					<Button mode="text">Lihat Detil</Button>
				</Card.Actions>
			</Card>

			<FAB
				icon="plus"
				label="Gabung Kelas"
				style={{ position: 'absolute', right: 30, bottom: 30 }}
				onPress={() => navigation.navigate('KelasScreen')}
			/>
		</>
	);
}

export default withTheme(HomeScreen)
