import { useState } from 'react';
import { Text, View } from 'react-native';
import { Appbar, Button, Modal, Portal, Provider, TextInput } from 'react-native-paper';

export default function CariKelasScreen({ navigation }) {
	const [visible, setVisible] = useState(false);
	const showModal = () => setVisible(true);
	const hideModal = () => setVisible(false);
	const containerStyle = { backgroundColor: 'white', padding: 20, marginHorizontal: 30 };


	return (
		<>
			<Appbar.Header>
				<Appbar.BackAction onPress={() => navigation.goBack()} />
				<Appbar.Content title="Gabung Kelas" />
			</Appbar.Header>

			<Portal>
				<Modal visible={visible} onDismiss={hideModal} contentContainerStyle={containerStyle}>
					<Text>Example Modal.  Click outside this area to dismiss.</Text>
				</Modal>
			</Portal>

			<View style={{ flexDirection: 'row' }}>
				<TextInput
					label="Ketikkan Kode Kelas"
					type="outlined"
				/>
				<Button icon="folder-search" mode="contained" onPress={showModal} style={{ margin: 10 }}>Cari</Button>
			</View>
		</>
	);
}
