import { useEffect, useState } from 'react';
import { ScrollView, Text, View } from 'react-native';
import { Appbar, Button, Card, TextInput } from 'react-native-paper';
import { supabase } from '../../config/supabase';

export default function CariKelasScreen({ navigation }) {
	const [visible, setVisible] = useState(false);
	const [kode, setKode] = useState('');
	const [kelas, setKelas] = useState([]);

	const cariKelas = async () => {
		const { data, error } = await supabase
			.from('kelas')
			.select('id, label, deskripsi)')
			.eq('kode', kode)
			.single();

		if (data) {
			setKelas(data);
			console.log('testing data:', data);
		}
		setVisible(true);
	}

	return (
		<>
			<Appbar.Header>
				<Appbar.BackAction onPress={() => navigation.goBack()} />
				<Appbar.Content title="Gabung Kelas" />
			</Appbar.Header>

			<ScrollView>
				<View style={{ flexDirection: 'row', margin: 20 }}>
					<TextInput
						label="Kode Kelas"
						mode="outlined"
						value={kode}
						onChangeText={val => setKode(val)}
					/>
					<Button mode="contained" onPress={() => cariKelas()} style={{ margin: 10 }}>Cari</Button>
				</View>
			</ScrollView>

			{visible && (
				<Card style={{ margin: 10 }}>
					<Card.Content>
						{(kelas !== '' || kelas !== NULL) ? (<>
							<Text variant="titleLarge" style={{ fontWeight: 'bold' }}>{kelas.label}</Text>
							<Text variant="bodyLarge" style={{ marginVertical: 30 }}>{kelas.deskripsi}</Text>
						</>
						) : (
							<Text variant="titleLarge" style={{ fontWeight: 'bold' }}>Kelas Tidak Ditemukan</Text>
						)}
					</Card.Content>
					<Card.Actions>
						<Button icon="arrow-right" mode="contained" onPress={() => navigation.navigate('HomeScreen')} contentStyle={{ flexDirection: 'row-reverse' }}>Gabung</Button>
					</Card.Actions>
				</Card>
			)}
		</>
	);
}
