import { View } from 'react-native';
import { Appbar, Button, Modal, Portal, Provider, TextInput } from 'react-native-paper';

export default function KelasScreen({ navigation }) {
	const [visible, setVisible] = React.useState(false);
	const showModal = () => setVisible(true);
	const hideModal = () => setVisible(false);
	const containerStyle = { backgroundColor: 'white', padding: 20 };


	return (
		<>
			<Portal>
				<Modal visible={visible} onDismiss={hideModal} contentContainerStyle={containerStyle}>
					<Text>Example Modal.  Click outside this area to dismiss.</Text>
				</Modal>
			</Portal>
			<Button style={{ marginTop: 30 }} onPress={showModal}>
				Show
			</Button>

			<Appbar.Header>
				<Appbar.BackAction onPress={() => navigation.goBack()} />
				<Appbar.Content title="Kelas" />
			</Appbar.Header>

			<View style={{ flexDirection: 'row' }}>
				<TextInput
					label="Ketikkan Kode Kelas"
					type="outlined"
				/>
				<Button icon="folder-search" mode="contained" onPress={() => navigation.navigate('KelasScreen')} style={{ margin: 10 }}>Cari</Button>
			</View>
		</>
	);
}
