import { useState } from 'react';
import { ScrollView, Text, View } from 'react-native';
import { Appbar, Button, Card, Modal, Portal, Provider, TextInput } from 'react-native-paper';
import HomeScreen from './HomeScreen';

export default function CariKelasScreen({ navigation }) {
	const [visible, setVisible] = useState(false);
	const showModal = () => setVisible(true);
	const hideModal = () => setVisible(false);
	const containerStyle = { backgroundColor: 'white', padding: 30, marginHorizontal: 30 };


	return (
		<>
			<Appbar.Header>
				<Appbar.BackAction onPress={() => navigation.goBack()} />
				<Appbar.Content title="Gabung Kelas" />
			</Appbar.Header>

			<ScrollView>
				<View style={{ flexDirection: 'row', margin: 20 }}>
					<TextInput
						label="Kode Kelas"
						mode="outlined"
					/>
					<Button mode="contained" onPress={showModal} style={{ margin: 10 }}>Cari</Button>
				</View>
			</ScrollView>

			{visible && (
				<Card style={{ margin: 10 }}>
					<Card.Content>
						<Text variant="titleMedium">Akademi Bank Sampah</Text>

						<View style={{ marginVertical: 15 }}>
							<Text variant="labelSmall">Perkembangan: 70%</Text>
						</View>

						<Text>Kelas ini mengajarkan peserta untuk memiliki pengetahuan & keterampilan dalam pengolahan sampah</Text>
					</Card.Content>
					<Card.Actions>
						<Button mode="text">Lihat Detil</Button>
					</Card.Actions>
				</Card>

			)}
		</>
	);
}
